from drf_haystack.serializers import HaystackSerializerfrom rest_framework import serializersfrom orders.models import OrderInfofrom orders.models import OrderGoodsfrom .models import SKUfrom .search_indexes import SKUIndexclass SKUSerializer(serializers.ModelSerializer):    """商品列表界面"""    class Meta:        model = SKU        fields = ['id', 'name', 'price', 'default_image_url', 'comments']class SKUSearchSerializer(HaystackSerializer):    """    SKU索引结果数据序列化器    """    object = SKUSerializer(read_only=True)    class Meta:        index_classes = [SKUIndex]        fields = ('text', 'object')class OrderSkuSerializer(serializers.ModelSerializer):    """订单sku序列化器"""    class Meta:        model = SKU        fields = ['name',  'default_image_url']class OrderGoodsSerializer(serializers.ModelSerializer):    """订单商品序列化器"""    sku = OrderSkuSerializer(read_only=True)    class Meta:        model = OrderGoods        fields = ['count', 'price', 'sku']class OrderCenterSerializer(serializers.ModelSerializer):    """订单中心序列化器"""    skus = OrderGoodsSerializer(many=True)    create_time = serializers.DateTimeField(format="%Y-%m-%d %H:%M:%S")    class Meta:        model = OrderInfo        fields = ['create_time', 'order_id', 'skus', 'total_amount', 'freight', 'pay_method', 'status']