from django.conf.urls import urlfrom . import viewsurlpatterns = [    # 获取扫码url    url(r'^qq/authorization/$', views.QQAuthURLView.as_view()),    # 扫码后回调处理    url(r'^qq/user/$', views.QQAuthUserView.as_view()),    # 获取微博登录url    url(r'^weibo/authorization/$', views.WeiboAuthURLView.as_view()),    # 微博登陆后回调    url(r'^sina/user/$', views.WeiboAuthUserView.as_view()),    # 生成图片验证码    url(r'^image_codes/(?P<code_id>.+)/$', views.GenerateImgCodeView.as_view()),    # 生成短信验证码    url(r'^sms_codes/(?P<mobile>1[3-8]\d{9})/$', views.GenerateSmsCodeView.as_view()),]